import{blogData}from"./data-blog.min.js";function blogPosts(t){const e=document.getElementById("blog");for(let l=0;l<t.length;l++){let a=document.createElement("div");a.classList.add("blog__item"),a.innerHTML=`<a href="" class="blog__article"> ${t[l].name} </a>\n        <div class="blog__info"> \n            <div class="blog__date">${t[l].time} </div>\n            <span> | </span> \n            <div class="blog__category">${t[l].subject} </div>\n        </div>\n        <div class="blog__text text"> ${t[l].message} </div>`,e.appendChild(a)}}blogPosts(blogData);