import{blogData}from"./data-blog.min.js";function blogPosts(s){const t=document.getElementById("blog");for(let o=0;o<s.length;o++){let n=document.createElement("div");n.classList.add("blog__item"),n.innerHTML=` \n        <div class="blog-post__container">\n            <div class="blog-post__content">\n                <div class="blog-post__info">\n                    <div class = "blog-post__theme"> ${s[o].subject} </div>\n                    <div class = "blog-post__date"> ${s[o].time} </div>\n                </div>\n                <div class="blog-post__title">${s[o].name}</div>\n                <div class="blog-post__text text">\n                    ${s[o].message}\n                </div>\n            </div>\n            <div class="blog-post__image">\n                <img src="${s[o].imageSrc}" alt="${s[o].imageAlt}">\n            </div>\n        </div>\n        <div class="blog-post__buttons">\n            <div class="blog-post__up-arrow"></div>\n            <div class="blog-post__down-arrow"></div>\n        </div>\n        `,t.appendChild(n)}}blogPosts(blogData);